INSERT INTO `512Database`.Product (id, publicPrice, purchasePrice, title, descriptionProduct, brand, imageURL, note) VALUES (1, 100.00, 50.00, 'Canon EOS 5D Mark III', 'The Canon EOS 5D Mark III is a full-frame 22.3-megapixel CMOS digital single-lens reflex camera made by Canon. It is the successor to the Canon EOS 5D Mark II, and was announced on 2 September 2012 with a suggested retail price of US$3,799. It was released on 21 November 2012.', 1, 'https://cdn.lesnumeriques.com/optim/produits/1/12892/canon-eos-5d-mark-iii__450_400.jpg', '3.5');
INSERT INTO `512Database`.Product (id, publicPrice, purchasePrice, title, descriptionProduct, brand, imageURL, note) VALUES (2, 100.00, 50.00, 'Canon EOS 5D Mark IV', 'The Canon EOS 5D Mark IV is a full-frame 30.4-megapixel CMOS digital single-lens reflex camera made by Canon. It is the successor to the Canon EOS 5D Mark III, and was announced on 25 August 2016 with a suggested retail price of US$3,799. It was released on 5 September 2016.', 1, 'https://d1fmx1rbmqrxrr.cloudfront.net/cnet/optim/i/edit/2018/08/canon_eos_5DM4_770__w1200.jpg', '3.3');
INSERT INTO `512Database`.Product (id, publicPrice, purchasePrice, title, descriptionProduct, brand, imageURL, note) VALUES (3, 100.00, 50.00, 'Canon EOS 6D Mark II', 'The Canon EOS 6D Mark II is a 26.2-megapixel full-frame CMOS DSLR camera made by Canon. It is the successor to the Canon EOS 6D, and was announced on 16 August 2017 with a suggested retail price of US$1,999. It was released on 17 September 2017.', 1, 'https://cdn.lesnumeriques.com/optim/produits/1/39933/canon-eos-6d-mark-ii-eos-6d-ii_e45659cf38280353__450_400.jpg', '3.3');
INSERT INTO `512Database`.Product (id, publicPrice, purchasePrice, title, descriptionProduct, brand, imageURL, note) VALUES (4, 100.00, 50.00, 'Canon EOS 80D', 'The Canon  EOS 80D Mark II is a 26.2-megapixel full-frame CMOS DSLR camera made by Canon. It is the successor to the Canon EOS 6D, and was announced on 16 August 2017 with a suggested retail price of US$1,999. It was released on 17 September 2017.', 1, 'https://www.canon.fr/for_home/product_finder/cameras/digital_slr/eos-80d/assets/images/360/A_032.png', '5.0');
