INSERT INTO `512Database`.Product (id, publicPrice, purchasePrice, title, descriptionProduct, brand, imageURL, note) VALUES (10, 800.00, 550.00, 'a6100', 'Immortalisez et partagez vos portraits et moments inoubliables. Avec une qualité d''image et une réactivité épatantes basées sur la technologie du plein format, l''appareil se charge du plus difficile : avec l''a6100, vous ne raterez plus un moment clé.', 3, 'https://www.imaging-resource.com/PRODS/sony-a6100/ZPR-SONY-A6100-BEAUTY.JPG', '4.3');
INSERT INTO `512Database`.Product (id, publicPrice, purchasePrice, title, descriptionProduct, brand, imageURL, note) VALUES (11, 900.00, 600.00, 'a6400', 'L''a6400 offre des performances rapides et une gamme de fonctionnalités dignes d''un modèle plein format tout en restant léger et compact. Images fixes ou films, compositions créatives ou selfies de tous les jours, les images sont superbes et dignes de partage dans tous vos déplacements.', 3, 'https://www.juzaphoto.com/shared_files/recensioni/sony_a6400.jpg', '4.6');
INSERT INTO `512Database`.Product (id, publicPrice, purchasePrice, title, descriptionProduct, brand, imageURL, note) VALUES (12, 1000.00, 650.00, 'a6600', 'L''a6600 réunit toutes les fonctionnalités dont vous pouvez avoir besoin quelles que soient les circonstances : une mise au point automatique rapide, un stabilisateur d''image fiable, une qualité d''image remarquable, une batterie haute capacité et des outils performants pour la réalisation de films 4K HDR (HLG).', 3, 'https://static.photocdn.pt/images/articles/2019/09/06/sony_a6600_review.jpg', '4.5');
INSERT INTO `512Database`.Product (id, publicPrice, purchasePrice, title, descriptionProduct, brand, imageURL, note) VALUES (13, 1000.00, 700.00, 'LUMIX S DC-S5', 'Le LUMIX S5 est un réel concentré de technologies à la fois en photo et en vidéo. Retrouvez la qualité d’un capteur plein format dans un boîtier compact et léger. Le LUMIX S5 est votre nouvel outil de création.', 4, 'https://www.bhphotovideo.com/images/images2500x2500/panasonic_dc_s5mk_lumix_dcs5_mirrorless_digital_1583509.jpg', '3.1');
INSERT INTO `512Database`.Product (id, publicPrice, purchasePrice, title, descriptionProduct, brand, imageURL, note) VALUES (14, 900.00, 500.00, 'LUMIX G DC-G100', 'Image et audio de qualité élevée s’associent à un fonctionnement simple dans un boîtier compact et pratique. Il s''agit de l''appareil photo numérique mono-objectif sans miroir (DSLM) spécialement conçu pour les créateurs de contenu.', 4, 'https://media1.test-achats.be/images/A7A74FD8DEEAC6D450D73A69137E1B3F28802980/c4/Appareils-photo-PANASONIC-LUMIX-DC-G100-plus-LUMIX-G-VARIO-12-32-zoom.jpeg', '3.7');
INSERT INTO `512Database`.Product (id, publicPrice, purchasePrice, title, descriptionProduct, brand, imageURL, note) VALUES (15, 800.00, 600.00, 'LUMIX S DC-S1H', 'Appareil Hybride Plein Format expert cinéma doté de l’enregistrement vidéo 6K 24p, du 4:2:2 10bit en C4K 30p interne, et de la fonction Double ISO natif. Plage dynamique 14+ stops et V-Log/V-Gamut intégrés pour des vidéos de qualité cinématographique.', 4, 'https://www.testatinromania.ro/wp-content/uploads/2019/06/panasonic-lumix-s1h.jpg', '4.2');
INSERT INTO `512Database`.Product (id, publicPrice, purchasePrice, title, descriptionProduct, brand, imageURL, note) VALUES (16, 1100.00, 800.00, 'LUMIX S DC-S1R', 'Un nouveau capteur CMOS 47,3 MP qui permet d''obtenir des détails incroyablement réalistes. Des systèmes de mise au point et de stabilisation d''image qui capturent chaque instant avec une netteté étonnante. L''appareil est conçu pour transformer n''importe quelle idée en photographies inoubliables. Grâce à toutes ces caractéristiques, et bien plus encore, cet appareil photo plein format vous emmène dans le monde extraordinaire de la photographie.', 4, 'https://www.testatinromania.ro/wp-content/uploads/2019/06/panasonic-lumix-s1h.jpg', '3.9');
